                  <React.Fragment>
                    <Box sx={{ mx: 1, fontSize: '14px' }}> From </Box>
                    <input ref={startProps.inputRef} {...startProps.inputProps} className='dateRange' />
                    <Box sx={{ mx: 1, fontSize: '14px' }}> to </Box>
