              {conversation?.tutor_id}{" "}
              <span
                style={{
                  color: "green",
                  fontWeight: "800",
                  marginLeft: "5px",
                  fontSize: "13px",
                  cursor: 'pointer',
                }}
              >
                 <HtmlTooltip
                    placement="top"
                    title={
                      <>
                        <span style={{ marginBottom: "10px" }}>
                          Sessions Done:
                        </span>
                        {conversation?.sessions_done?.map((el) => (
                            <Typography
                              color="inherit"
                              style={{
                                fontSize: "13.5px",
                                fontWeight: "600",
                                marginTop: "5px",
                              }}
                            >
                              &#8226;{" "}
                              <span
                                style={{
                                  fontSize: "12px",
                                  fontWeight: "normal",
                                }}
                              >
                                {el}
                              </span>
                            </Typography>
                          ))}
                      </>
                    }
                  >
                    <span>{conversation?.sessions_done &&
                    '[' +
                      conversation?.sessions_done?.filter((c, index, arr) => {
                        return arr.indexOf(c) === index;
                      }).length +
                      ']'}
                    </span>
                  </HtmlTooltip>
              </span>
              <Box
                sx={{
                  width: 200,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "flex-end",
                }}
              >
                <Rating
                  name="read-only"
                  size="small"
                  value={conversation?.rating / 2}
                  precision={0.1}
                  emptyIcon={
                    <StarIcon style={{ opacity: 0.55 }} fontSize="inherit" />
                  }
                  readOnly
                />
                &nbsp;
                <span style={{ fontSize: "13px", fontWeight: "700" }}>
                  {("" + conversation?.rating / 2)?.slice(0, 4)}
                </span>
                <span>
                  <HtmlTooltip
                    placement="top"
                    title={
                      <>
                        <span style={{ marginBottom: "10px" }}>
                          Last 5 reviews
                        </span>
                        {conversation?.rating_and_reviews
                          ?.slice(
                            Math.max(
                              conversation?.rating_and_reviews?.length - 5,
                              0
                            )
                          )
                          ?.map((el) => (
                            <Typography
                              color="inherit"
                              style={{
                                fontSize: "13.5px",
                                fontWeight: "600",
                                marginTop: "5px",
                              }}
                            >
                              -{" "}
                              <span
                                style={{
                                  fontSize: "12px",
                                  fontWeight: "normal",
                                }}
                              >
                                {el.reviews}
                              </span>
                            </Typography>
                          ))}
                      </>
                    }
                  >
                    <InfoIcon
                      fontSize="small"
                      style={{ fontSize: "16px", cursor: "pointer" }}
                    />
                  </HtmlTooltip>
                </span>
              </Box>
            </div>
          </div>
          <div
            style={{
              display: "flex",
              flex: "10%",
              flexDirection: "column",
              justifyContent: "center",
            }}
          >
            <div
              style={{
                display: "flex",
                flexDirection: "row",
                alignSelf: "flex-end",
                cursor: "pointer",
              }}
            >
              <span style={{ paddingRight: "5px" }}>
                {showMenu ? (
                  <KeyboardArrowUpIcon
                    fontSize="medium"
                    color="action"
                    onClick={handleClick}
                  />
                ) : (
                  <KeyboardArrowDownIcon
                    fontSize="medium"
                    color="action"
                    onClick={handleClick}
                  />
                )}
              </span>
            </div>
          </div>
        </div>
      </div>
      {showMenu && (
        <div className="conversationDetails">
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              margin: "7px",
            }}
          >
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              Highest Degree: <span>{conversation?.highest_degree}</span>
            </span>
            {(conversation?.tutor_status === "active" ) ? (
              <span
                style={{ marginTop: "-8px", cursor: "pointer" }}
                onClick={handleToggle}
              >
                {console.log({valTestConv:conversation?.tutor_status, checked})}
                {/* <Switch
                Active
                checked={checked}
                onChange={handleChangeStatus}
                inputProps={{ 'aria-label': 'controlled' }}
                size='small'
              /> */}
                <ToggleOnIcon fontSize="large" />
              </span>
            ) : (conversation?.tutor_status == "inactive") ? (
              <span
                style={{ marginTop: "-8px", cursor: "pointer" }}
                onClick={handleToggle}
              >
                <ToggleOffIcon fontSize="large" color="disabled" />
              </span>
            ) : null}
            <Snackbar
              open={openSnackBar}
              autoHideDuration={2000}
              onClose={handleCloseSnackBar}
              anchorOrigin={{ vertical, horizontal }}
              key={vertical + horizontal}
            >
              <Alert
                onClose={handleCloseSnackBar}
                severity="info"
                sx={{ width: "100%" }}
              >
                {alertText}
              </Alert>
            </Snackbar>
          </div>
          {reasonDialog && (
            <Reason
              setReason={setReason}
              setReasonVal={setReasonVal}
              setReasonDialog={setReasonDialog}
              reasonDialog={reasonDialog}
              visibleCircularbar={visibleCircularbar}
            />
          )}
          <div style={{ margin: "15px 7px 7px 7px" }}>
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              College:{" "}
              <span>
                {conversation?.academic_info &&
                  conversation?.academic_info[0]?.college}
              </span>
            </span>
          </div>
          <div style={{ margin: "23px 7px 7px 7px" }}>
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              WhatsApp: <span>{conversation?.wa_id}</span>
              {/* { !editPhoneVal ? 
              (<span>{conversation?.wa_id}</span>)
              :   (<span>
                    <input
                      type="number"
                      style={{
                        backgroundColor: "aliceblue",
                        width: "30%",
                        height: "20px",
                        marginTop: "5px",
                        outline: "none",
                        border: "none",
                        borderBottom: "1px solid gray",
                        borderRadius: "0px",
                      }}
                      value={phoneVal}
                      onChange={(val) => setPhoneVal(val.target.value)}
                      autoFocus
                    />{" "}
                    &nbsp;
                    <button
                      style={{
                        backgroundColor: "#1876D1",
                        color: "#ffff",
                        border: "none",
                        borderRadius: "2px",
                        height: "25px",
                        cursor: "pointer",
                      }}
                      onClick={(e)=>handlePhoneChange(e,conversation?.tutor_id)}
                    >
                      Update
                    </button>
                </span>)
              }
              <span style={{ marginLeft: "3px" }}>
                <EditIcon
                  fontSize="x-small"
                  style={{ fontSize: "15px" }}
                  onClick={(e) => handleEditPhone(e,conversation?.wa_id)}
                />
              </span> */}
            </span>
          </div>
          <div style={{ margin: "23px 7px 7px 7px" }}>
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              Email: <span>{conversation?.email}</span>
              {/* { !editEmailVal ? 
              (<span>{conversation?.email}</span>)
              :   (<span>
                    <input
                      type="email"
                      style={{
                        backgroundColor: "aliceblue",
                        width: "50%",
                        height: "20px",
                        marginTop: "5px",
                        outline: "none",
                        border: "none",
                        borderBottom: "1px solid gray",
                        borderRadius: "0px",
                      }}
                      value={emailVal}
                      onChange={(val) => setEmailVal(val.target.value)}
                      autoFocus
                    />{" "}
                    &nbsp;
                    <button
                      style={{
                        backgroundColor: "#1876D1",
                        color: "#ffff",
                        border: "none",
                        borderRadius: "2px",
                        height: "25px",
                        cursor: "pointer",
                      }}
                      onClick={(e)=>handleEmailChange(e,conversation?.tutor_id)}
                    >
                      Update
                    </button>
                </span>)
              }
              <span style={{ marginLeft: "3px" }}>
                <EditIcon
                  fontSize="x-small"
                  style={{ fontSize: "15px" }}
                  onClick={(e) => handleEditEmail(e,conversation.email)}
                />
              </span> */}
            </span>
          </div>
          <div style={{ margin: "15px 7px 7px 7px" }}>
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              Subjects known:
            </span>
            {conversation?.subjects &&
              conversation?.subjects.map((i) => (
                <Chip style={{ margin: "6px" }} label={i} />
              ))}
          </div>
          <div style={{ margin: "15px 7px 7px 7px" }}>
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              Add tag:
            </span>
            {conversation?.tags &&
              conversation?.tags.map((i) => (
                <Chip style={{ margin: "6px" }} label={i} color="primary" />
              ))}

            <Fab
              style={{ height: "36px", width: "36px" }}
              color="primary"
              aria-label="add"
            >
              <AddIcon onClick={handleClickOpen} />
              <Dialog open={openTag} onClose={_handleClose}>
                <DialogContent>
                  <DialogContentText>Add New Tag</DialogContentText>
                  <TextField
                    autoFocus
                    margin="dense"
                    id="name"
                    label="Tag Name"
                    type="text"
                    value={tagValue}
                    onChange={(e) => setTagValue(e.target.value)}
                    fullWidth
                    variant="standard"
                  />
                </DialogContent>
                <DialogActions>
                  <Button onClick={_handleClose}>Cancel</Button>
                  <Button onClick={_handleAddTag}>Add</Button>
                </DialogActions>
              </Dialog>
            </Fab>
          </div>
          <div style={{ display: "flex", margin: "15px 7px 7px 7px" }}>
            <span
              style={{
                fontSize: "14px",
                fontWeight: "500",
                marginRight: "5px",
              }}
            >
              Writer:
            </span>
            {conversation?.writer  ? (
              <span
                style={{ marginTop: "-8px", cursor: "pointer" }}
                onClick={() =>
                  handleChangeWriterStatus(conversation?.tutor_id, false)
                }
              >
                <ToggleOnIcon fontSize="large" color="primary" />
              </span>
            ) : !conversation?.writer  ? (
              <span
                style={{ marginTop: "-8px", cursor: "pointer" }}
                onClick={() =>
                  handleChangeWriterStatus(conversation?.tutor_id, true)
                }
              >
                <ToggleOffIcon fontSize="large" color="disabled" />
              </span>
            ) : null}
          </div>
          {/* <div style={{ margin: '15px 7px 7px 7px' }}>
            <span
              style={{
                fontSize: '14px',
                fontWeight: '500',
                marginRight: '5px',
              }}
            >
              Rate this tutor:{' '}
              <span>
                <input
                  type='number'
                  style={{ width: '15%', marginLeft: '5px', outline: 'none' }}
                  min='1'
                  max='10'
                  value={ratingVal}
                  // onKeyDown={(evt) => evt.preventDefault()}
                  onChange={(e) => setRatingVal(e.target.value)}
                />{' '}
                &nbsp;
                <button
                  style={{
                    backgroundColor: '#1876D1',
                    color: '#ffff',
                    border: 'none',
                    borderRadius: '2px',
                    height: '21px',
                    cursor: 'pointer',
                  }}
                  onClick={rateHandler}
                >
                  rate
                </button>
              </span>
            </span>
          </div> */}
        </div>
      )}
    </>
  );
};

export default TutorContact;
