      param1: sessionType + " Assigned",
      param2: tutorName,
      param3: sessionId,
      param4: `d${deviceNo}/tutorForm/${sessionId}/${tutorId}?accept_task=success`,
    };
    try {
      const res = await axios.post(
        `${localStorage.getItem(
          "api"
        )}/api/sessions/tutorAssignMailNotification`,
        payload,
        {
          headers: "",
        }
      );
      setUpdated(!updated);
    } catch (err) {
      console.log(err);
    }
