  const handleCancelTutor = async (tutorId, sessionId) => {
    try {
      const payload = {
        status: true,
      };
      const res = await axios.put(
        `${localStorage.getItem(
          "api"
        )}/api/sessions/cancelNotifiedTutor/${sessionId}/${tutorId}`,
        payload,
        {
          headers: "",
        }
      );
      setUpdated(!updated);
    } catch (err) {
      console.log(err);
    }
  };

